import { useState,useEffect,useContext,createContext } from "react";
import { Link } from "react-router-dom";

import StyleNavBar from "./StyleNavbar.module.css";

import Logo from "../../assets/imagem/caverta-logo1.png"

const MyContext =  createContext();

export const ProviderContext=({children})=>{

const[ButtonMenu, setButtonMenu] = useState(false);

return(

<MyContext.Provider value={{ ButtonMenu, setButtonMenu }}>
{children}
</MyContext.Provider>

)

}

export function NavBar(){

const { ButtonMenu, setButtonMenu } = useContext(MyContext);
const [Nav,setNav] = useState(false);
const [Scroll, setScroll] = useState(0);
const [Width, setWidth] = useState(window.innerWidth);

const handleButtonClick = () => {
setButtonMenu(!ButtonMenu);
};

useEffect(()=>{

if(Scroll > 100){
setNav(true)

}else{

setNav(false)

}

const handleScroll = () => {
setScroll(window.scrollY);
};

const handleWidth = () => {
setWidth(window.innerWidth);
};

  window.addEventListener('scroll', handleScroll);
  window.addEventListener('resize', handleWidth);

console.log(Nav)
console.log("valor scroll",Scroll)
console.log("lagura da tela ",Width)
console.log("menuMobileAtivo",ButtonMenu)

return () => {
  window.removeEventListener('scroll', handleScroll);
  window.removeEventListener('resize', handleWidth);
};

},[Scroll, Nav, Width,ButtonMenu])


const Navegacao =()=>{

if(Width >= 760){

return <div className={StyleNavBar.ConteinerNavBar}>

<div className={StyleNavBar.ConteinerLogo}>
    
    <div className={StyleNavBar.BoxLogo}>
        <img src={Logo} alt="Logo"/>
    </div>

</div>

<nav className={StyleNavBar.ConteinerNav}>

    <div className={StyleNavBar.BoxNav}>

        <ul>
            <li><Link to={"/"}>Home</Link></li>
            <li><Link to={"/Cardapio"}>Cardapio</Link></li>
            <li><Link to={"/Sobre"}>Sobre</Link></li>
            <li><Link to={"/Contato"}>Contato</Link></li>
            <li><Link to={"/Reservas"}>Reservas</Link></li>

        </ul>

    </div>

</nav>

</div>

}else{

return <div className={StyleNavBar.ConteinerNavBar}>

<div className={StyleNavBar.ConteinerLogo}>
    
    <div className={StyleNavBar.BoxLogo}>
        <img src={Logo} alt="Logo"/>
    </div>

</div>

<nav className={StyleNavBar.ConteinerNav}>

    <div className={StyleNavBar.BoxNav}>

        <ul>
            <li><Link to={"/"}>Home</Link></li>
            <li><Link to={"/Cardapio"}>Cardapio</Link></li>
            <li><Link to={"/Sobre"}>Sobre</Link></li>
            <li><Link to={"/Contato"}>Contato</Link></li>
            <li><Link to={"/Reservas"}>Reservas</Link></li>

        </ul>

    </div>

</nav>

</div>

}


}

