import { useEffect, useState, useRef } from "react";
import StyleHomePage from "./StyleHome.module.css";

import Slide1 from "../../assets/imagem/About-us-title-img.jpg";
import Slide2 from "../../assets/imagem/slider-2.jpg";
import Slide3 from "../../assets/imagem/slider-3.jpg";

export default function HomePage(){

const[CurrentItem, setCurrentItem] = useState(0);
const ConteinerSlide = useRef();

useEffect(()=>{

if(CurrentItem > 2){

setCurrentItem(0)

}if(CurrentItem<0){
   
setCurrentItem(2)

}

if (ConteinerSlide.current) {
    // Acessa e manipula diretamente o elemento DOM
    ConteinerSlide.current.style.transform = `translateX(-${CurrentItem * 100}%)`; 
}

console.log(CurrentItem)

},[CurrentItem])

function BtnLeft(){

    setCurrentItem(CurrentItem-1);

}

function BtnRight(){

    setCurrentItem(CurrentItem+1);

}

return(

<>

<head>

<link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.3/dist/css/bootstrap.min.css" rel="stylesheet" integrity="sha384-QWTKZyjpPEjISv5WaRU9OFeRpok6YctnYmDr5pNlyT2bRjXh0JMhjY6hW+ALEwIH" crossorigin="anonymous"></link>
    
</head>

<main>

<section id={StyleHomePage.Home}> 

<div className={StyleHomePage.ConteinerHome}>

    <div id={StyleHomePage.ConteinerText}>

        <div className={StyleHomePage.boxText}>

            <h4>HAPPY HOURS AT</h4>
            <h1>Caverta</h1>
            <p>We offer a highly seasonal and continuously evolving tasting menu experience.</p>
            <button>Contact Us</button>

        </div>

    </div>

    <div id={StyleHomePage.conteinerCarrosel} ref={ConteinerSlide}>

        <div className={StyleHomePage.Slides} style={{background:"Slide1"}}>Slide1</div>
        <div className={StyleHomePage.Slides} style={{background:{Slide2}}}>Slide2</div>
        <div className={StyleHomePage.Slides} style={{background:{Slide3}}}>Slide3</div>

    </div>

<button className={StyleHomePage.BtnCarrosel} onClick={BtnLeft}>
<i class="bi bi-chevron-compact-left"></i>
</button>

<button className={StyleHomePage.BtnCarrosel} onClick={BtnRight} style={{right:"0"}}>
<i class="bi bi-chevron-compact-right"></i>
</button>

</div>

</section>

<section> 
</section>

<section> 
</section>

<section> 
</section>

<section> 
</section>

<section> 
</section>

<section> 
</section>

<section> 
</section>

</main>

</>

)

}
